import {Component, OnInit, ElementRef, Renderer2, ViewChild} from '@angular/core';
import {BlogService} from "../service/blog.service";
import {ActivatedRoute, ParamMap} from "@angular/router";
import {switchMap} from "rxjs/operators";
import {DomSanitizer, SafeResourceUrl} from "@angular/platform-browser";
import {NONE_TYPE} from "@angular/compiler";
import {async} from "rxjs";



const cache = {};

@Component({
  selector: 'app-article',
  templateUrl: './article.component.html',
  styleUrls: ['./article.component.css']
})
export class ArticleComponent implements OnInit {

  article: any;
  markUrl: SafeResourceUrl;

  @ViewChild('divContent') greetDiv: ElementRef;

  constructor(
    private sanitizer:DomSanitizer,
    private route: ActivatedRoute,
    private blogService: BlogService,
    private el: ElementRef,
    private renderer:Renderer2
  ) {}


  ngOnInit(): void {
    const {owner, repo} = this.blogService.getDefaultOwnerAndRepo();
    this.route.params.subscribe(
      (result: any) => {
        // let url = `https://raw.githubusercontent.com/${owner}/${repo}/main/${result.articleName}`;
        // this.markUrl = this.sanitizer.bypassSecurityTrustResourceUrl(url);
        this.blogService.getBlog$(owner, repo, result.articleName)
          .subscribe(
            (data:any) => {
              if(!data)
                this.article = data;
              else
                this.article = this.sanitizer.bypassSecurityTrustHtml(data);

              // set style
              if(screen.width<500){
                this.renderer.setStyle(this.greetDiv.nativeElement, 'font-size', '0.7rem');
              }
            }
          );
      }
    );
  }

  getDomTest() {
    this.renderer.setStyle(document.getElementById('write'), 'padding', '0.7rem');
    // this.renderer.setStyle(this.greetDiv.nativeElement.querySelector('p'), 'padding-left', '0.7rem');
    console.log(document.getElementById('write'));
  }

  ngAfterViewInit():void{
    console.log(document.body);
    while (document.getElementById('write')==null){
      ArticleComponent.sleep(500);
      console.log('sleep')
    }
    console.log(document.getElementById('write'));
  }

  static async sleep(ms: number) {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve('');
      }, ms)
    });
  }

  async test(){
    await ArticleComponent.sleep(500);
  }

}
<nz-skeleton class="item" *ngIf='!article' [nzActive]="true" [nzParagraph]="{ rows: 20}" (ended)="getDomTest()" ></nz-skeleton>
<div #divContent>
  <div class="article" *ngIf="article" [innerHTML]="article" (click)="getDomTest()"></div>
</div>
